import React, { useState, useEffect } from 'react';

export default function Stats(props) {
  const [currStats, setCurrStats] = useState(null);
  const [prevStats, setPrevStats] = useState(null);

  let tempStats = null;
  useEffect(() => {
    if (currStats) {
      tempStats = {
        algorithmUsed: currStats.algorithmUsed,
        pathLength: currStats.pathLength,
        nodesChecked: currStats.nodesChecked,
      };
    }
    if (props.algorithmUsed !== null) {
      setCurrStats({
        algorithmUsed: props.algorithmUsed,
        pathLength: props.pathLength,
        nodesChecked: props.nodesChecked,
      });
    } else {
      setPrevStats(null);
      setCurrStats(null);
    }
  }, [props]);
  useEffect(() => {
    if (currStats) {
      setPrevStats(tempStats);
    }
  }, [currStats]);

  return (
    <React.Fragment>
      <div className="stats-container">
        {currStats ? (
          <React.Fragment>
            <p className="label">Current Run: </p>
            <p>Alogrithm used: {currStats.algorithmUsed}</p>
            <p>Path Length: {currStats.pathLength}</p>
            <p>Nodes Visited: {currStats.nodesChecked - 1}</p>
          </React.Fragment>
        ) : null}
      </div>

      <div className="prev-stats-container">
        {prevStats ? (
          <React.Fragment>
            <p className="label">Previous Run: </p>
            <p>Alogrithm used: {prevStats.algorithmUsed}</p>
            <p>Path Length: {prevStats.pathLength}</p>
            <p>Nodes Visited: {prevStats.nodesChecked - 1}</p>
          </React.Fragment>
        ) : null}
      </div>
    </React.Fragment>
  );
}
