import React, { Component } from "react";
import "./node.css";

class Node extends Component {
  constructor(props) {
    super(props);
    this.setStart = this.setStart.bind(this);
    this.setEnd = this.setEnd.bind(this);
    this.setWall = this.setWall.bind(this);

    this.state = {
      nodeType: "",
    };
  }

  componentDidMount() {
    let nodeType = this.props.node.nodeType;
    this.setState({ nodeType });
  }

  setStart() {
    if (this.props.startNodeSet === false) {
      this.props.handleStartNodeSet(this.props.node.row, this.props.node.col);
      let nodeType = "start-node";
      this.setState({ nodeType });
    }
  }

  setEnd() {
    let nodeType = "end-node";
    this.setState({ nodeType });
  }

  setWall() {
    let nodeType = "wall-node";
    this.setState({ nodeType });
  }

  render() {
    return (
      <div
        className={this.state.nodeType}
        onClick={this.getClickHandler()}
      ></div>
    );
  }

  getClickHandler() {
    let clickHandler = this.setWall;
    if (this.props.selectionMode === "start") {
      clickHandler = this.setStart;
    } else if (this.props.selectionMode === "end") {
      clickHandler = this.setEnd;
    }

    return clickHandler;
  }
}

export default Node;
