import React, { Component } from "react";
import Node from "./node/node";

class PathfinderVisualiser extends Component {
  state = {
    nodes: [],
  };

  render() {
    this.generateNodes();
    let nodes = this.state.nodes;
    return (
      <div>
        {nodes.map(() => {
          return <Node></Node>;
        })}
        <h1 style={{ color: "blue", fontSize: 150 }}>Mimi very Mushy</h1>
      </div>
    );
  }

  generateNodes() {
    let nodes = [];
    for (let i = 0; i < 15; i++) {
      let currentRow = [];
      for (let j = 0; j < 15; j++) {
        nodes.push(currentRow);
      }
    }

    this.setState({ nodes });
  }
}

export default PathfinderVisualiser;
