import React, { useState, useEffect, useRef } from 'react';

export default function Stats(props) {
  const [currentStats, setCurrentStats] = useState({});
  const [previousStats, setPreviousStats] = useState({});
  const prevStatsRef = useRef();

  useEffect(() => {
    prevStatsRef.current = props;
  }, [props]);

  const prevStats = prevStatsRef.current;

  console.log(props);
  return (
    <React.Fragment>
      <div className="stats-container">
        {props.pathLength && props.nodesChecked ? (
          <React.Fragment>
            <p>Alogrithm used: {props.algorithmUsed}</p>
            <p>Path Length: {props.pathLength}</p>
            <p>Nodes Visited: {props.nodesChecked - 1}</p>
          </React.Fragment>
        ) : null}
      </div>

      <div className="stats-container">
        {prevStats.pathLength && prevStats.nodesChecked ? (
          <React.Fragment>
            <p>Alogrithm used: {prevStats.algorithmUsed}</p>
            <p>Path Length: {prevStats.pathLength}</p>
            <p>Nodes Visited: {prevStats.nodesChecked - 1}</p>
          </React.Fragment>
        ) : null}
      </div>
    </React.Fragment>
  );
}
