import React, { Component } from "react";
import "./header.css";

class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      nodes: [],
      isStartHovered: false,
      isEndHovered: false,
      isWallHovered: false,
    };
    this.handleEventHover = this.handleEventHover.bind(this);
  }

  handleEventStartHover() {
    this.setState({ isStartHovered: !this.state.isStartHovered });
  }

  handleEventEndHover() {
    this.setState({ isEndHovered: !this.state.isEndHovered });
  }

  handleEventWallHover() {
    this.setState({ isWallHovered: !this.state.isWallHovered });
  }

  render() {
    const startBtnClass = this.state.isHovered
      ? "button-hovered"
      : "button-default";
    const endBtnClass = this.state.isHovered
      ? "button-hovered"
      : "button-default";
    const wallBtnClass = this.state.isHovered
      ? "button-hovered"
      : "button-default";

    console.log(btnClass);

    return (
      <div>
        <h1>Visual Pathfinder</h1>
        <ul className="button-container">
          <li
            className={btnClass}
            onClick={this.props.startClick}
            onMouseEnter={this.handleEventHover}
            onMouseLeave={this.handleEventHover}
          >
            Start
          </li>
          <li className={btnClass} onClick={this.props.endClick}>
            End
          </li>
          <li className={btnClass} onClick={this.props.wallClick}>
            Wall
          </li>
        </ul>
      </div>
    );
  }
}

export default Header;
