import React, { Component } from "react";
import "./header.css";

class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      nodes: [],
      isHovered: false,
    };
    this.handleEventHover = this.handleEventHover.bind(this);
  }

  handleEventHover() {
    this.setState((prevState) => {
      isHovered: !prevState.isHovered;
    });
  }

  render() {
    const btnClass = this.state.isHovered ? "button-hovered" : "button-default";
    return (
      <div>
        <h1>Visual Pathfinder</h1>
        <ul className="button-container">
          <li
            className="node-type"
            onClick={this.props.startClick}
            onMouseEnter={this.handleEventHover}
          >
            Start
          </li>
          <li className="node-type" onClick={this.props.endClick}>
            End
          </li>
          <li className="node-type" onClick={this.props.wallClick}>
            Wall
          </li>
        </ul>
      </div>
    );
  }
}

export default Header;
