import React, { Component } from "react";
import Node from "./node/node";
import "./grid.css";

class Grid extends Component {
  constructor(props) {
    super(props);
    this.handleNodeClick = this.handleNodeClick.bind(this);
  }

  state = {
    nodes: [],
  };

  componentDidMount() {
    let nodes = [];
    for (let row = 0; row < 15; row++) {
      let currentRow = [];
      for (let col = 0; col < 30; col++) {
        let currentNode = {
          row: row,
          col: col,
          nodeType: "normal-node",
        };
        currentRow.push(currentNode);
      }
      nodes.push(currentRow);
    }
    this.setState({ nodes });
  }

  handleNodeClick(newRow, newColumn) {
    let nodeSelectMode = this.props.selectionMode;
    let nodes = this.state.nodes;

    if (nodeSelectMode === "start") {
      let rowIndex = -1;
      let colIndex = -1;
      for (let i = 0; i < nodes.length; i++) {
        colIndex = nodes[i].findIndex((currentColumn) => {
          return currentColumn.nodeType === "start-node";
        });

        if (rowIndex !== -1) {
          rowIndex = i;
          break;
        }
      }

      if (rowIndex !== -1 && colIndex !== -1) {
        nodes[rowIndex][colIndex].nodeType = "normal-node";
        nodes[newRow][newColumn].nodeType = "start-node";

        this.setState({ nodes });
      }
    } else if (nodeSelectMode === "end") {
    } else {
      console.log(newRow, newColumn);
      console.log(nodes[3]);
      nodes[3][newColumn].nodeType = "wall-node";
      this.setState({ nodes });
    }
  }

  render() {
    const { nodes } = this.state;

    return (
      <div className="grid">
        {nodes.map((row, rowIndex) => {
          return (
            <div key={rowIndex}>
              {row.map((node, colIndex) => {
                return (
                  <Node
                    key={colIndex}
                    node={node}
                    selectionMode={this.props.selectionMode}
                    handleNodeClick={this.handleNodeClick}
                  ></Node>
                );
              })}
            </div>
          );
        })}
      </div>
    );
  }
}

export default Grid;
