import React, { Component } from "react";
import "./node.css";

class Node extends Component {
  constructor(props) {
    super(props);
    this.handleStartClick = this.handleStartClick.bind(this);
    this.handleEndClick = this.handleEndClick.bind(this);
    this.handleWallClick = this.handleWallClick.bind(this);

    this.state = {
      nodeType: "",
    };
  }

  componentDidMount() {
    let nodeType = this.props.node.nodeType;
    this.setState({ nodeType });
  }

  handleStartClick() {
    this.props.handleNodeClick();
  }

  handleEndClick() {
    let nodeType = "end-node";
    this.setState({ nodeType });
  }

  handleWallClick() {
    this.props.handleNodeClick(this.props.node.row, this.props.node.col);
  }

  render() {
    return (
      <div
        className={this.props.node.nodeType}
        onClick={this.getClickHandler()}
      ></div>
    );
  }

  getClickHandler() {
    let clickHandler = this.handleWallClick;
    if (this.props.selectionMode === "start") {
      clickHandler = this.handleStartClick;
    } else if (this.props.selectionMode === "end") {
      clickHandler = this.handleEndClick;
    }

    return clickHandler;
  }
}

export default Node;
