import React, { Component } from "react";
import "./header.css";
import Buttons from "./buttons/buttons";
import Grid from "./grid/grid";

class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      nodes: [],
      selectionMode: "",
      resetGridCalled: false,
      isStartHovered: false,
      isEndHovered: false,
      isWallHovered: false,
      isResetHovered: false,
      isGoHovered: false,
      buttonPressed: "",
    };
    this.handleStartHover = this.handleStartHover.bind(this);
    this.handleEndHover = this.handleEndHover.bind(this);
    this.handleWallHover = this.handleWallHover.bind(this);
    this.handleResetHover = this.handleResetHover.bind(this);
    this.handleGoHover = this.handleGoHover.bind(this);

    this.handleStartClick = this.handleStartClick.bind(this);
    this.handleEndClick = this.handleEndClick.bind(this);
    this.handleWallClick = this.handleWallClick.bind(this);
    this.handleResetClick = this.handleResetClick.bind(this);
    this.handleGoClick = this.handleGoClick.bind(this);

    this.gridRef = React.createRef(); // used to handle reseting the grid. Need to access the method from here
  }

  handleStartHover() {
    this.setState({ isStartHovered: !this.state.isStartHovered });
  }

  handleEndHover() {
    this.setState({ isEndHovered: !this.state.isEndHovered });
  }

  handleWallHover() {
    this.setState({ isWallHovered: !this.state.isWallHovered });
  }

  handleResetHover() {
    this.setState({ isResetHovered: !this.state.isResetHovered });
  }

  handleGoHover() {
    this.setState({ isGoHovered: !this.state.isGoHovered });
  }

  handleStartClick() {
    this.setState({ selectionMode: "start", buttonPressed: "start" });
  }

  handleEndClick() {
    this.setState({ selectionMode: "end", buttonPressed: "end" });
  }

  handleWallClick() {
    this.setState({ selectionMode: "wall", buttonPressed: "wall" });
  }

  handleResetClick() {
    this.gridRef.current.resetGrid();
    this.setState({ selectionMode: "", buttonPressed: "reset" });
  }

  handleGoClick() {
    this.gridRef.current.visualiseDijkstras();
    this.setState({ buttonPressed: "go" });
  }

  render() {
    return (
      <React.Fragment>
        <Grid
          selectionMode={this.state.selectionMode}
          resetGrid={this.state.resetGridCalled}
          ref={this.gridRef}
        />
      </React.Fragment>
    );
  }

  getBtnClass(button) {
    let btnClass = "";

    if (button === "start") {
      btnClass = this.state.isStartHovered
        ? "button-hovered"
        : "button-default";

      if (this.state.buttonPressed === "start") {
        btnClass = "button-clicked";
      }
    } else if (button === "end") {
      btnClass = this.state.isEndHovered ? "button-hovered" : "button-default";

      if (this.state.buttonPressed === "end") {
        btnClass = "button-clicked";
      }
    } else if (button === "wall") {
      btnClass = this.state.isWallHovered ? "button-hovered" : "button-default";

      if (this.state.buttonPressed === "wall") {
        btnClass = "button-clicked";
      }
    } else if (button === "reset") {
      btnClass = this.state.isResetHovered
        ? "button-hovered"
        : "button-default";
    } else if (button === "go") {
      btnClass = this.state.isGoHovered ? "button-hovered" : "button-default";

      if (this.state.buttonPressed === "go") {
        btnClass = "button-clicked";
      }
    }

    return btnClass;
  }
}

export default Header;
