import React, { Component } from "react";
import Node from "./node/node";

class Grid extends Component {
  state = {
    nodes: [],
  };

  render() {
    const { nodes } = this.state;
    console.log(nodes);
    return (
      <div>
        {nodes.map(() => {
          return <Node></Node>;
        })}
      </div>
    );
  }

  componentDidMount() {
    let nodes = [];
    for (let i = 0; i < 15; i++) {
      let currentRow = [];
      for (let j = 0; j < 15; j++) {
        currentRow.push([]);
      }
      nodes.push(currentRow);
    }
    this.setState({ nodes });
  }
}

export default Grid;
